import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';

import { Box, Container, Typography } from '@mui/material'



const PostDetail = () => {
    const [post, setPost] = useState('');

    const id = useParams().id
    const url = `http://localhost:5000/posts/${id}`
    const fetchPost = async () => {
        const res = await axios.get(url).catch(err => console.log(err.message))
        const data = await res.data

        return data;
    }

    useEffect(() => {
        fetchPost()
            .then(data=> setPost(data.post))

    }, [id]);



 return (
    <>

      <Container sx={{width: 900, marginBottom: 10}}>
        <Typography variant="h3" component='h1' marginTop={3} textA>{post.title}</Typography>
          <Box marginTop={3} sx={{display: 'flex', justifyContent: 'space-between' /*overflow: 'hidden'*/}}>
        <img src={post.image} alt={post.title} />

      </Box>
      <Box>
        <Typography variant='h6' component='h4' marginTop={3}>
          About this Ticket...
        </Typography>
        <Typography variant='paragraph' component='p' marginTop={3}>
          Hexagon food truck vinyl, ugh cray direct trade 8-bit stumptown truffaut bushwick kombucha keffiyeh shaman keytar. Pok pok master cleanse small batch echo park, kickstarter cloud bread messenger bag neutra. Hella forage mustache mlkshk. Flexitarian food truck health goth cliche, tattooed chartreuse bespoke ugh quinoa tilde authentic vice.
        </Typography>
      </Box>
      <Box>
        <Typography variant='h6' component='h4' marginTop={3}>
          Frequently asked Questons
        </Typography>


      </Box>
      </Container>


      </>
  )
}

export default PostDetail
